mapper.update:
update t_equipment
equipmentNo =#{equipmentNo}, equipmentName =#{equipmentName}, category = #{category}, model=#{model}, status=#{status}, factory=#{factory}, techFeature=#{techFeature}, unit=#{unit}, count=#{count}, productDate=#{productDate}, productNo=#{productNo}, buyDate=#{buyDate}, useDate=#{useDate}, inputDate=#{inputDate}, validDate=#{validDate}, department=#{department}, principal=#{principal}, address=#{address}
where id = #{id}

mapper.select:
select * from t_equipment

	 and equipmentNo = #{equipmentNo}	
	 and equipmentName like '%${equipmentName}%'	
	 and model like '%${model}%'	
	 and category like '%${catetory}%'	
	 and productNo = #{productNo}	
	 and factory = #{factory}	
	 and department = #{department}
	 and status = #{status}
